import java.util.*;class Challenge{String weightedSort(String t){var r=new java.util.TreeMap<String,Integer>(Collections.reverseOrder());for(var s:t.replaceAll("['-]","").split("\\P{L}+")){r.put(s,s.chars().sum());}var z=new String[]{""};r.entrySet().stream().sorted(Collections.reverseOrder(Map.Entry.comparingByValue())).forEach(e->z[0]+=e.getKey()+"|"+e.getValue()+"\n");return z[0];}}